'use client';

import React, { useState, useEffect } from 'react';
import { Analytics } from "@vercel/analytics/react";
import { useRouter } from 'next/navigation'
import Image from "next/image";
import './pages.css';
// import OverlayCard from './overlaycard';


export default function Home() {
  const router = useRouter()

  const [loading, setLoading] = useState(false);
  const [wordCount, setWordCount] = useState(0);
  const [humanizedTextWordCount, setHumanizedTextWordCount] = useState(0);
  const [text, setText] = useState('');
  const [humanizedText, setHumanizedText] = useState('');
  const [toggleCopy, setToggleCopy] = useState(false);
  const [isUsedOneTime, setIsUsedOneTime] = useState(false);

  useEffect(() => {
    const usedOneTime = localStorage.getItem('isUsedOneTime') === 'true';
    setIsUsedOneTime(usedOneTime);
  }, []);

  const humanaizeAiText = async (aiText: string) => {
    console.log('Sending POST request /api/humanaize');
    try {
      const response = await fetch('/api/humanaize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ aiText }),
      });

      if (!response.ok) {
        // throw new Error('API request failed');
        console.log('API request failed');
      }

      const data = await response.json();
      return data.message;
    } catch (error) {
      console.log('An error occurred while fetching the reply.');
      console.error('Error:', error);
      // alert('An error occurred while fetching the reply.');
      // return 'No response available';
      // send request to /api/gpt
      console.log('Trying GPT...');
      return tryGpt(aiText);
    } finally {
      console.log('POST request /api/humanaize completed');
    }
  };

  const tryGpt = async (aiText: string) => {
    console.log('Sending POST request /api/gpt');
    try {
      const response = await fetch('/api/gpt', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ aiText }),
      });

      if (!response.ok) {
        // throw new Error('API request failed');
        console.log('API request failed');
      }

      const data = await response.json();
      return data.message;
    } catch (error) {
      console.log('An error occurred while fetching the reply.');
      console.error('Error:', error);
      alert('An error occurred while fetching the reply.');
      return 'No response available';
    } finally {
      console.log('POST request /api/gpt completed');
    }
  };

  const handleHumanize = () => {
    console.log('Humanizing...');
    setLoading(true);
    tryGpt(text)
      .then((humanized) => {
        setHumanizedText(humanized);
        const count = humanized.trim() === '' ? 0 : humanized.trim().split(/\s+/).length;
        setHumanizedTextWordCount(count);
      })
      .catch((error) => {
        console.error('Error:', error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  useEffect(() => {
    const count = text.trim() === '' ? 0 : text.trim().split(/\s+/).length;
    setWordCount(count);
  }, [text]);

  const loader = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="1.5em" height="1.5em" viewBox="0 0 24 24">
      <circle cx={4} cy={12} r={3} fill="currentColor">
        <animate id="svgSpinners3DotsScale0" attributeName="r" begin="0;svgSpinners3DotsScale1.end-0.25s" dur="0.75s" values="3;.2;3" />
      </circle>
      <circle cx={12} cy={12} r={3} fill="currentColor">
        <animate attributeName="r" begin="svgSpinners3DotsScale0.end-0.6s" dur="0.75s" values="3;.2;3" />
      </circle>
      <circle cx={20} cy={12} r={3} fill="currentColor">
        <animate id="svgSpinners3DotsScale1" attributeName="r" begin="svgSpinners3DotsScale0.end-0.45s" dur="0.75s" values="3;.2;3" />
      </circle>
    </svg>
  );

  return (
    <div className="grid grid-rows-[20px_1fr_20px] items-center justify-items-center min-h-screen pb-28 gap-8 p-4 font-[family-name:var(--font-geist-sans)]">
      <Analytics />
      <main className="flex flex-col gap-8 row-start-2 items-center">
        <div className="Editor_title3__hWeAn ">
          <h1 className="Editor_editor__header__GIEq6 font-bold bg-white dark:bg-background dark:text-white"><span className='italic font-black'>HumanAIze </span>AI text</h1>
          <h2 className="Editor_editor__subheader__uH98a font-bold bg-white dark:bg-background dark:text-white">HumanAIze AI text with the smartest AI humanizer</h2>
          <p className="Editor_editor__description__sjr_h bg-white dark:bg-background dark:text-white">Transform your AI-generated content into natural, human-like text with the ultimate HumanAIze AI text tool. This ai-to-human text converter effortlessly converts output from ChatGPT, Bard, Jasper, Grammarly, GPT4, and other AI text generators into text indistinguishable from human writing. Achieve 100% originality and enhance your content creation with the best HumanAIze AI solution available.</p>
          <div className='bg-background text-black dark:bg-foreground p-2'>
            <a
              className="flex items-center"
              href="https://aimlapi.com/?via=ibrohim"
              target="_blank"
              rel="noopener noreferrer"
            >
              <Image 
                src="/powered_by_aimlapi.svg"
                alt="Powered by AIML API"
                width={120}
                height={120}
              />
            </a>
          </div>
        </div>
        <div className="flex gap-2 items-center flex-col lg:flex-row">
          <div className="bg-white dark:bg-background p-4">
            <div className="relative">
              <textarea
                value={text}
                onChange={(e) => setText(e.target.value)}
                className="w-[600px] h-[400px] p-4 pb-12 dark:bg-background dark:text-white border border-solid border-gray-600 rounded-lg focus:outline-none resize-none focus:ring-2 focus:ring-[#333] focus:border-transparent"
                placeholder="Paste your AI-generated text here"
              ></textarea>
              <div className="absolute bottom-4 left-4 dark:bg-foreground dark:text-black bg-black text-white px-2 py-1 rounded-md text-sm text-gray-100 shadow">
                {wordCount} words
              </div>
              <button
                className={`absolute bottom-4 right-4 dark:bg-foreground dark:text-black bg-black text-white rounded-md shadow border border-solid border-transparent transition-colors flex items-center justify-center bg-foreground text-gray-100 gap-2 hover:bg-[#aeaeae] dark:hover:bg-[#aeaeae] text-sm sm:text-base h-10 sm:h-12 px-4 sm:px-5 
                  ${(loading || text.trim().length < 100) ? 'cursor-not-allowed opacity-50' : 'cursor-pointer'}
                `}
                onClick={text.trim().length > 100 ? handleHumanize : undefined}
                disabled={loading}
              >
                {loading ? loader() : 'Humanize'}
              </button>
            </div>
          </div>
          <div className="bg-white dark:bg-background p-4">
            <div className="relative">
              <textarea 
                disabled={true}
                value={humanizedText}
                className="bg-white p-4 w-[600px] h-[400px] p-4 dark:bg-background dark:text-white border border-solid border-gray-600 rounded-lg focus:outline-none outline-none resize-none focus:ring-2 focus:ring-[#333] focus:border-transparent" 
                placeholder="Humanized text will appear here">
              </textarea>
              <div className="absolute bottom-4 left-4 dark:bg-foreground dark:text-black bg-black text-white px-2 py-1 rounded-md text-sm text-gray-100 shadow">
                {humanizedTextWordCount} words
              </div>
              <button 
                disabled={(!loading) ? false : true}
                className={`absolute bottom-4 right-4 dark:bg-foreground dark:text-black bg-black text-white flex flex-row gap-1 items-center bg-foreground px-2 py-1 rounded-md text-sm text-gray-100 shadow 
                  ${(loading) ? 'cursor-not-allowed opacity-50' : 'cursor-pointer'}
                `}
                onClick={() => {
                  if (!loading) {
                    navigator.clipboard.writeText(humanizedText);
                    setToggleCopy(true);
                    setTimeout(() => {
                      setToggleCopy(false);
                    }, 1000);
                  }
                }}
              >
                <Image
                  aria-hidden
                  className='dark:bg-black'
                  src={!toggleCopy ? '/copy.svg' : '/check.svg'}
                  alt="Copy icon"
                  width={16}
                  height={16}
                /> 
                copy
              </button>
            </div>
          </div>
        </div>
      </main>
      <footer className="row-start-3 flex flex-col gap-6 flex-wrap items-center justify-center mt-8">
        <div className='flex gap-6 flex-wrap items-center justify-center bg-white dark:bg-background dark:text-white p-2'>
          <a
            className="flex items-center gap-2 hover:underline hover:underline-offset-4"
            href="https://twitter.com/abdibrokhim"
            target="_blank"
            rel="noopener noreferrer"
          >
            <Image
              aria-hidden
              className='dark:bg-white'
              src="/X_logo_2023_original.svg"
              alt="X icon"
              width={16}
              height={16}
            />
            X (prev. Twitter)
          </a>
          <a
            className="flex items-center gap-2 hover:underline hover:underline-offset-4"
            href="https://www.<b>linkedin.com/in/abdibrokhim</b>/"
            target="_blank"
            rel="noopener noreferrer"
          >
            <Image
              aria-hidden
              className='dark:bg-white'
              src="/LinkedIn_icon.svg"
              alt="LinkedIn icon"
              width={16}
              height={16}
            />
            LinkedIn
          </a>
          <a
            className="flex items-center gap-2 hover:underline hover:underline-offset-4"
            href="https://github.com/abdibrokhim/humanaize"
            target="_blank"
            rel="noopener noreferrer"
          >
            <Image
              aria-hidden
              className='dark:bg-white'
              src="/github-mark.svg"
              alt="Github icon"
              width={16}
              height={16}
            />
            Open Source ‚Üí
          </a>
        </div>
      </footer>
      <script
        src="https://topmate-embed.s3.ap-south-1.amazonaws.com/v1/topmate-embed.js"
        user-profile="https://topmate.io/embed/profile/abdibrokhim?theme=D5534D"
        btn-style='{"backgroundColor":"#fff","color":"#000","border":"1px solid #000"}'
        embed-version="v1"
        button-text="Hey, dear! Let's chat."
        position-right="30px"
        position-bottom="30px"
        custom-padding="0px"
        custom-font-size="16px"
        custom-font-weight="500"
        custom-width="200px"
      ></script>
    </div>
  );
}

import React, { useEffect, useState, useRef } from 'react';
import { firebaseConfig } from './firebaseConfig';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDiscord, faGithub, faGoogle, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import { faArrowsRotate, faCircleDollarToSlot, faEnvelope } from '@fortawesome/free-solid-svg-icons';
import { onAuthStateChanged } from 'firebase/auth';
import { initializeApp } from 'firebase/app';
import { getAuth, GoogleAuthProvider, signInWithPopup, signOut } from 'firebase/auth';
import { User } from 'firebase/auth';
import Notification from './notify';
import { GuessedCard } from './types';
import { getFirestore, collection, getDocs, setDoc, doc, deleteDoc, updateDoc, getDoc } from 'firebase/firestore';
import getStripe from './api/utils/get-stripe';

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const provider = new GoogleAuthProvider();
const firestore = getFirestore(app);

export default function Header() {
    const [user, setUser] = useState<User | null>(null);
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [showLoginCard, setShowLoginCard] = useState(false);
    const [showProfile, setShowProfile] = useState(false);
    const dropdownRef = useRef<HTMLDivElement | null>(null);
    const loginCardRef = useRef<HTMLDivElement | null>(null); // Reference for login card
    const profileCardRef = useRef<HTMLDivElement | null>(null); // Reference for login card
    const [points, setPoints] = useState<number>(0);
    const [notification, setNotification] = useState<{ message: string; type: 'error' | 'success' |'info' } | null>(null);  // notification message
    const [needSubscription, setNeedSubscription] = useState<boolean>(true);

    // if user is not logged in, show login card every 2 minutes
    useEffect(() => {
        const interval = setInterval(() => {
            if (!user) {
                setShowLoginCard(true);
            }
        }, 60000);
        return () => clearInterval(interval);
    }, [user]);

    // show notification
    const triggerNotification = (nMessage: string, nType: 'error' | 'success' | 'info') => {
        setNotification({ message: nMessage, type: nType });
    };

    const fetchGuessedCards = async () => {
        if (!user || !user.email) {
            console.error("User is not logged in or email is missing");
            triggerNotification("User not authenticated", "error");
            return;
        }
        const userEmail = user.email;
        const querySnapshot = await getDocs(collection(firestore, 'guessedCards', userEmail, 'cards'));
        const cards: GuessedCard[] = [];
        querySnapshot.forEach((doc) => {
            const data = doc.data();
            const gCard: GuessedCard = {
            id: data.id,
            imageUrl: data.imageUrl,
            prompt: data.prompt,
            guess: data.guess,
            grade: data.grade,
            point: data.point,
            createdAt: data.createdAt,
            };
            cards.push(gCard);
        });
        console.log("guessed cards: ", cards);
        // calculate total points
        const totalPoints = cards.reduce((acc, card) => acc + card.point, 0);
        setPoints(totalPoints);
        console.log("total points: ", totalPoints);
    };

    // get guessed cards from firebase database
    useEffect(() => {
        console.log("fetching guessed cards...");
        console.log("user: ", user);
        console.log("user email: ", user?.email);
        fetchGuessedCards();
    }, [user]);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
            setUser(currentUser);
        });

        return () => unsubscribe();
    }, [user]);

    const getCurrentUser = () => {
        return new Promise((resolve, reject) => {
            const unsubscribe = onAuthStateChanged(auth, (user) => {
                resolve(user);
                unsubscribe();
            }, reject);
        });
    };

    const handleLogout = () => {
        signOut(auth);
        setDropdownOpen(false); // Close the dropdown on logout
    };

    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    // Close the dropdown if clicked outside
    useEffect(() => {
        const handleClickOutside = (event: any) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setDropdownOpen(false);
            }
            if (loginCardRef.current && !loginCardRef.current.contains(event.target as Node)) {
                setShowLoginCard(false);
            }
            if (profileCardRef.current && !profileCardRef.current.contains(event.target as Node)) {
                setShowProfile(false);
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const handleStripe = async () => {
        const checkoutSession = await fetch('/api/checkout_sessions', {
            method: 'POST',
            headers: { origin: 'http://localhost:3000' },
        })
        const checkoutSessionJson = await checkoutSession.json()

        const stripe = await getStripe()
        const {error} = await stripe.redirectToCheckout({
            sessionId: checkoutSessionJson.id,
        })

        if (error) {
          console.warn(error.message)
        }
    };

    // fetch user subscription from firebase collection=subscriptions where email=user.email. save to state subscribeed
    const fetchUserSubscription = async () => {
        // const currentUser = await getCurrentUser();

      if (!user || !user.email) {
        console.error("User is not logged in or email is missing");
        triggerNotification("User not authenticated", "error");
        return;
      }

      const userEmail = user.email;
      const docRef = doc(firestore, 'subscriptions', userEmail);

      try {
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
            setNeedSubscription(false);
        } else {
            setNeedSubscription(true);
        }
      } catch (error) {
        console.error("Error fetching user subscription: ", error);
        triggerNotification("Error fetching subscription status", "error");
      }
    };

    // get user subscription from firebase database
    useEffect(() => {
        console.log("fetching user subscription...");
        console.log("user: ", user);
        console.log("user email: ", user?.email);
        fetchUserSubscription();
    }, [user]);

    // login component
    const Login = () => {
        const handleGoogleLogin = async () => {
            try {
                await signInWithPopup(auth, provider);
                setShowLoginCard(false); // Close the login card after successful login
            } catch (error) {
                console.error('Error signing in with Google: ', error);
            }
        };

        return (
            <div className="flex flex-row gap-4 flex-wrap justify-center m-auto" ref={loginCardRef}>
                <button
                    onClick={handleGoogleLogin}
                    className="cursor-pointer hover:underline border border-white rounded-md px-4 py-4 flex items-center gap-2"
                >
                    <FontAwesomeIcon icon={faGoogle} />
                    Sign in with Google
                </button>
            </div>
        );
    };

    // profile component
    const Profile = () => {
        return (
            <div className="flex flex-col gap-4 justify-center m-auto p-4" ref={profileCardRef}>
                <div className="flex flex-row gap-4">
                        <div className="flex-shrink-0">
                            <img
                                className="h-12 w-12 rounded-full"
                                src={user?.photoURL || 'https://via.placeholder.com/150'}
                                alt={user?.displayName!}
                            />
                        </div>
                        <div>
                            <h1 className="text-lg font-bold">{user?.displayName}</h1>
                            <p className="text-sm text-gray-400">{user?.email}</p>
                        </div>
                </div>
                <div className="w-full lg:max-w-5xl flex items-center justify-between p-2 shadow-lg gap-4 bg-[#2e2e2e] rounded-md">
                    <p className='text-xs text-[#aaaaaa]'>Points recieved: <span className='text-sm text-white font-bold'>{points}</span></p>
                    <button
                        onClick={() => {
                            triggerNotification('Refreshing points...', 'info');
                            fetchGuessedCards();
                        }}
                        className={`flex items-center justify-center w-6 h-6 rounded-full shadow cursor-pointer bg-[#eeeeee] hover:bg-[#aaaaaa] text-black`}
                        >
                        <FontAwesomeIcon icon={faArrowsRotate} />
                    </button>
                </div>
                {needSubscription ? (
                    <div className='text-sm text-[#aaaaaa]'>
                        <button 
                            className='text-md text-white font-bold underline'
                            onClick={
                                () => {
                                    handleStripe();
                                }}
                                >SUBSCRIBE
                        </button>
                        <span> to get <span className='text-md text-white font-bold italic'>100</span> generations for a month.</span>
                    </div>
                    ) : (
                        <p className='text-sm text-[#aaaaaa]'>Your current plan <span className='text-md text-white font-bold'>USD $10/month</span></p>
                    )
                }
            </div>
        )
    };

    return (
        <header className="flex flex-col gap-4 justify-between items-end p-4 text-white absolute top-4 right-2">
            {/* show notification */}
            {notification && (
                <Notification
                message={notification.message}
                type={notification.type}
                onClose={() => setNotification(null)}
                />
            )}
            {/* show login card as a modal */}
            {showLoginCard && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
                    <div className="bg-[#2e2e2e] rounded-lg shadow-lg max-w-[800px] max-h-[600px] p-2">
                        <Login />
                    </div>
                </div>
            )}
            {/* show profile card as a modal */}
            {showProfile && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
                    <div className="bg-[#2e2e2e] rounded-lg shadow-lg max-w-[800px] max-h-[600px] p-2">
                        <Profile />
                    </div>
                </div>
            )}
            <div>
                {user ? (
                    <div className="relative" ref={dropdownRef}>
                        <span
                            onClick={toggleDropdown}
                            className="cursor-pointer hover:underline border border-white rounded-md px-4 py-4"
                        >
                            {user.email}
                        </span>
                        {dropdownOpen && (
                            <div className='absolute right-0 mt-6 gap-4 bg-[#2e2e2e] rounded-lg shadow-lg p-2'>
                                <div className="rounded-md shadow-lg z-10">
                                    <button
                                        onClick={() => setShowProfile(true)}
                                        className="block w-full text-white text-center px-4 py-2 hover:bg-[#aaaaaa] hover:text-black rounded-md"
                                    >
                                        Profile
                                    </button>
                                </div>
                                <div className="rounded-md shadow-lg z-10">
                                    <button
                                        onClick={handleLogout}
                                        className="block w-full text-white text-center px-4 py-2 hover:bg-[#aaaaaa] hover:text-black rounded-md"
                                    >
                                        Logout
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                ) : (
                    <button
                        onClick={() => setShowLoginCard(true)}
                        className="block w-full bg-[#eeeeee] text-black text-center px-4 py-2 hover:bg-[#aaaaaa] rounded-md"
                    >
                        Login
                    </button>
                )}
            </div>
        <div className="flex flex-col gap-2 items-end justify-start mt-[200px]">
          <a href="mailto:abdibrokhim@gmail.com" className={`textButton flex items-center justify-start lg:justify-start my-1 transition text-gray-400 text-sm hover:text-white hover:underline hover:font-bold
          `}
            target="_blank" rel="noreferrer noopener">
            <i className="fas fa-fw fa-envelope mr-2"></i>
            <div className="text"><FontAwesomeIcon icon={faEnvelope} className='mr-2' /> contact</div>
          </a>
          <a href="https://buymeacoffee.com/abdibrokhim/" className={`textButton flex items-center justify-start lg:justify-start my-1 transition text-gray-400 text-sm hover:text-white hover:underline hover:font-bold
            `} target="_blank" rel="noreferrer noopener">
            <i className="fas fa-fw fa-donate mr-2"></i>
            <div className="text"><FontAwesomeIcon icon={faCircleDollarToSlot} className='mr-2' /> support</div>
          </a>
          <a href="https://github.com/abdibrokhim/AI-Magic-FlashCards-with-Stripe" className={`textButton flex items-center justify-start lg:justify-start my-1 transition text-gray-400 text-sm hover:text-white hover:underline hover:font-bold
            `} target="_blank" rel="noreferrer noopener">
            <i className="fas fa-fw fa-code mr-2"></i>
            <div className="text"><FontAwesomeIcon icon={faGithub} className='mr-2' /> github</div>
          </a>
          <a href="https://discord.gg/nVtmDUN2sR" className={`textButton flex items-center justify-start lg:justify-start my-1 transition text-gray-400 text-sm hover:text-white hover:underline hover:font-bold
            `} target="_blank" rel="noreferrer noopener">
            <i className="fab fa-fw fa-discord mr-2"></i>
            <div className="text"><FontAwesomeIcon icon={faDiscord} className='mr-2' /> discord</div>
          </a>
          <a href="https://<b>linkedin.com/in/abdibrokhim</b>" className={`textButton flex items-center justify-start lg:justify-start my-1 transition text-gray-400 text-sm hover:text-white hover:underline hover:font-bold
            `} target="_blank" rel="noreferrer noopener">
            <i className="fab fa-fw fa-linkedin mr-2"></i>
            <div className="text"><FontAwesomeIcon icon={faLinkedin} className='mr-2' /> linkedin</div>
          </a>
        </div>
        </header>
    );
}

